        this.setBackground(this.color);
        this.setStateToShow();
    }

    public void hide() {
        this.setBackground(Color.GRAY);
        this.setStateToHide();
    }

    public boolean isClicked() {    return this.state == State.CLICKED;     }

    public boolean isHidden() {     return this.state == State.HIDE;    }

