                this.input.requestFocus();      // Cursor ins Eingabefeld

                System.out.println("add geklickt : " + inputString);
            } 
            else if(src == this.btnRemove)
            {
                String inputString = this.input.getText();

                for(JLabel element : this.labels)
                {
